operation.add:
//paghiper pix pagamentos
$this->load->model('checkout/order');
if(isset($this->request->get['paghiperpix']) && $this->request->get['paghiperpix']=='true' && isset($this->request->get['id']) && isset($this->request->get['hash']) && sha1($this->request->get['id'])==$this->request->get['hash']){
	//pega os dados da transação pix 
	$query = $this->db->query("SELECT * FROM `".DB_PREFIX."paghiperpix_pedidos` WHERE transaction_id = '".$this->db->escape($this->request->get['id'])."'");
	if($query->row){
		$data['dados_paghiperpix'] = $query->row;
		$data['dados_pedido'] = $this->model_checkout_order->getOrder($query->row['id_pedido']);
	}
}
//paghiper pix pagamentos
